    int arr[SIZE];
    struct node *next;
};
b) #define SIZE N
struct node
{
    int arr[SIZE];
    struct node *prev;
    struct node *top;
};
c) #define SIZE N
struct node
{
    int node_count;
    struct node *next;
};
d) #define SIZE N
struct node
{
    int node_count;
    int arr[SIZE];
};
