using namespace std; 

void func(int a[], int n, int k) 
{ 
 if (k <= n) 
 { 
  for (int i = 0; i < k/2; i++) 
  swap(a[i], a[k-i-1]); 
 } 

} 
int main() 
{ 
 int a[] = {1, 2, 3, 4, 5}; 
 int n = sizeof(a) / sizeof(int), k = 3; 
