{  
 int count[n]; 
 memset(count, 0, sizeof(count)); 

 for (int i=n-2; i>=0; i--) 
 { 
  if (arr[i] >= n - i - 1) 
   count[i]++; 

  for (int j=i+1; j < n-1 && j <= arr[i] + i; j++) 

