What is the output of the following Java code?
public class CircularQueue
{
 protected static final int CAPACITY = 100;
 protected int size,front,rear;
 protected Object q[];
 int count = 0;

 public CircularQueue()
 {
  this(CAPACITY);
 }
 public CircularQueue (int n)
 {
  size = n;
  front = 0;
  rear = 0;
  q = new Object[size];
 }


 public void enqueue(Object item)
 {
  if(count == size)
  {
   System.out.println("Queue overflow");
    return;
  }
  else
  {
   q[rear] = item;
   rear = (rear+1)%size;
   count++;
  }
 }
 public Object dequeue()
 {
  if(count == 0)
  {
   System.out.println("Queue underflow");
   return 0;
  }
  else
  {
   Object ele = q[front];
   q[front] = null;
   front = (front+1)%size;
   count--;
   return ele;
  }
 }
 public Object frontElement()
 {
  if(count == 0)
  return -999;
  else
  {
   Object high;
   high = q[front];
   return high;
  }
 }
 public Object rearElement()
 {
  if(count == 0)
  return -999;
  else
  {
   Object low;
   rear = (rear-1)%size;
   low = q[rear];
   rear = (rear+1)%size;
   return low;
  }
 }
}
